function getOffset(el) {
    var top = el.offsetTop;
    var left = el.offsetLeft;
    while (el.offsetParent) {
        el = el.offsetParent;
        top += el.offsetTop;
        left += el.offsetLeft;
    }
    return {
        left: left,
        top: top
    }
}
